from selenium.common.exceptions import NoSuchElementExceptionfrom selenium import webdriverimport pytestfrom selenium.webdriver.common.by import Bydef test_basket_exists_on_page(request):    browser = webdriver.Chrome()    languages = {'ar', 'ca', 'cs', 'da', 'de', 'en - gb', 'el', 'es', 'fi', 'fr', 'it', 'ko', 'nl', 'pl', 'pt',                 'pt - br', 'ro', 'ru', 'sk', 'uk', 'zh - hans'}    link = f"http://selenium1py.pythonanywhere.com/{languages}/catalogue/coders-at-work_207/"    language = request.config.getoption("language")    language = None    if language == f"{languages}":        print(f"\nOpening site with {language} language")        browser.get(link)    else:        raise pytest.UsageError(f"--language should be one of {languages}")    basket = browser.find_element(By.ID, 'add_to_basket_form')    try:        assert basket.is_displayed()        print('Элемент "Добавить в корзину" присутствует на странице')    except NoSuchElementException:        print('Элемент "Добавить в корзину" отсутствует')